interface VideoCardProps {
    title: string;
    description: string;
    videoUrl: string;
}



function VideoCard({ title, description, videoUrl }: VideoCardProps) {
    const videoRef = useRef<HTMLVideoElement>(null);
    const containerRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        const videoElement = videoRef.current;
        const containerElement = containerRef.current;

        if (!videoElement || !containerElement) return;

        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        videoElement.play().catch(() => {
                            // Autoplay might be blocked by browser
                            console.log('Autoplay blocked');
                        });
                    } else {
                        videoElement.pause();
                    }
                });
            },
            {
                threshold: 0.5, // Trigger when 50% of the video is visible
            }
        );

        observer.observe(containerElement);

        return () => {
            observer.unobserve(containerElement);
        };
    }, []);

    return (
        <Card className="h-full shadow-none border-none bg-transparent w-full transition-transform duration-300 hover:scale-105">
            <CardContent className="p-0 relative" ref={containerRef}>
                <div className="relative aspect-video">
                    <video
                        ref={videoRef}
                        className="w-[30rem] shadow-none relative left-10  mx-auto rounded-xl h-[35rem] object-cover"
                        loop
                        muted
                        playsInline
                        preload="metadata"
                    >
                        <source src={videoUrl} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                </div>
               
            </CardContent>
        </Card>
    );
}